mapper.select:
SELECT enquiry_board_no enquiryBoardNo , member_id memberId , content , category , create_date createDate FROM enquiry_board
category like concat(#{category})
ORDER BY create_date DESC LIMIT #{startRow}, #{rowPerPage}

mapper.insert:
INSERT INTO enquiry_board(
	member_id
	, content
	, category
	, create_date
	, update_date
	) VALUES (
	#{memberId}
	, #{content}
	, #{category}
	, NOW()
	, NOW()
	)

mapper.update:
UPDATE enquiry_board SET 
	member_id = #{memberId}
	, content = #{content}
	, category = #{category}
	, update_date = NOW()
WHERE enquiry_board_no = #{enquiryBoardNo}

mapper.select:
SELECT member_id memberId
 , enquiry_answer_content enquiryAnswerContent
 ,  create_date createDate
FROM enquiry_answer
WHERE enquiry_board_no = #{enquiryBoardNo}
ORDER BY create_date DESC;

mapper.insert:
INSERT INTO enquiry_answer (
	 enquiry_board_no
	, member_id
	, enquiry_answer_content
	, create_date
	, update_date
) VALUES (
	 #{enquiryBoardNo}
	, #{memberId}
	, #{enquiryAnswerContent}
	, NOW()
	, NOW());
