mapper.update:
UPDATE homework_make SET
	homework_make_title = #{homeworkMakeTitle},homework_make_content = #{homeworkMakeContent},homework_deadline = #{homeworkDeadline}, update_date= now()
WHERE homework_make_no = #{homeworkMakeNo}

UPDATE homework_submission SET
	homework_score = #{homeworkScore}
WHERE homework_submission_no = #{homeworkSubmissionNo}

UPDATE homework_submission SET
	homework_submission_title = #{homeworkSubmissionTitle}, homework_submission_content=#{homeworkSubmissionContent}
WHERE homework_submission_no = #{homeworkSubmissionNo}

mapper.select:
SELECT hm.homework_make_no homeworkMakeNo ,hm.lecture_name lectureName ,hm.homework_make_title homeworkMakeTitle ,hm.homework_make_content homeworkMakeContent ,hm.homework_deadline homeworkDeadline ,hm.create_date createDate ,r.student_id ,hs.homework_score homeworkScore ,hs.homework_submission_no homeworkSubmissionNo FROM registration r INNER JOIN homework_make hm ON r.lecture_name = hm.lecture_name left JOIN homework_submission hs ON hs.student_id = r.student_id WHERE r.student_id = #{studentId}
AND hm.lecture_name = #{lectureName}
GROUP BY hm.homework_make_no ORDER BY hm.create_date desc

SELECT h.homework_make_no homeworkMakeNo ,h.lecture_name lectureName ,h.homework_make_title homeworkMakeTitle ,h.homework_make_content homeworkMakeContent ,h.homework_deadline homeworkDeadline ,h.create_date createDate ,l.registration_number registrationNumber FROM homework_make h INNER JOIN lec l ON h.lecture_name = l.lecture_name INNER JOIN lec_plan lp ON l.lecture_name = lp.lecture_name where lp.teacher_id = #{teacherId}
AND l.lecture_name = #{lectureName}
ORDER BY h.create_date desc
