mapper.select:
SELECT COUNT(*)
FROM lecture_notice
WHERE lecture_name = #{lectureName}

mapper.update:
UPDATE lecture_notice SET 
member_id=#{memberId}
,title=#{title}
,content=#{content}
,update_date=NOW()
WHERE lec_notice_no = #{lecNoticeNo}

mapper.select:
SELECT lecture_name lectureName
FROM lec_plan
WHERE lec_state = 'T' AND teacher_id = #{teacherId}

		SELECT lecture_name lectureName
		FROM registration
 		WHERE student_id = #{studentId}
